import React, { useState, useEffect, useRef } from 'react';

const ChatBot = () => {
  const [messages, setMessages] = useState([]);
  const [voiceInput, setVoiceInput] = useState('');
  const [confirmSpelling, setConfirmSpelling] = useState(false);
  const inputRef = useRef(null);

  const questions = [
    { id: 1, text: 'What is your name?' },
    { id: 2, text: 'What is your address?' },
    { id: 3, text: 'What is your date of birth?' },
    // Add more questions as needed
  ];

  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  const handleVoiceInput = (event) => {
    const transcript = event.results[0][0].transcript;
    setVoiceInput(transcript);
    handleUserInput(transcript);
  };

  const handleVoiceError = (event) => {
    console.log('Voice recognition error:', event.error);
  };

  const startVoiceRecognition = () => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;
    const recognition = new SpeechRecognition();
    recognition.interimResults = false;
    recognition.lang = 'en-US';
    recognition.addEventListener('result', handleVoiceInput);
    recognition.addEventListener('error', handleVoiceError);
    recognition.start();
  };

  const stopVoiceRecognition = () => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;
    const recognition = new SpeechRecognition();
    recognition.stop();
  };

  
  const handleUserInput = (event) => {
    event.preventDefault();
    const userInput = voiceInput;
    setMessages([...messages, { text: userInput, isUser: true }]);
    setVoiceInput('');

    // Handle user response
    if (confirmSpelling) {
      const nextQuestionIndex = currentQuestionIndex + 1;
      if (nextQuestionIndex < questions.length) {
        const nextQuestion = questions[nextQuestionIndex];
        setMessages([...messages, { text: nextQuestion.text, isUser: false }]);
        setCurrentQuestionIndex(nextQuestionIndex);
      } else {
        setMessages([...messages, { text: 'No more questions.', isUser: false }]);
      }
      setConfirmSpelling(false); // Reset the spelling confirmation
    } else {
      setMessages([...messages, { text: 'Please enter the correct spelling:', isUser: false }]);
      setConfirmSpelling(true); // Set spelling confirmation to true for the next response
    }

    // Clear the input field
    inputRef.current.value = '';
  };
  
  
  useEffect(() => {
    // Scroll to the bottom of the chat window whenever messages change
    const chatWindow = document.getElementById('chat-window');
    chatWindow.scrollTop = chatWindow.scrollHeight;
  }, [messages]);

  useEffect(() => {
    // Ask the initial question when the component mounts
    const currentQuestion = questions[currentQuestionIndex];
    setMessages([...messages, { text: currentQuestion.text, isUser: false }]);
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  return (
    <div>
      <div id="chat-window" style={{ height: '400px', overflow: 'scroll' }}>
      {messages.map((message, index) => (
  <div key={index} style={{ textAlign: message.isUser ? 'right' : 'left' }}>
    {message.text}
  </div>
))}

      </div>
      <form onSubmit={handleUserInput}>
      <input type="text" ref={inputRef} value={voiceInput} onChange={(e) => setVoiceInput(e.target.value)} />
        <button type="submit">Send</button>
      </form>
      <button onClick={startVoiceRecognition}>Start Voice Recognition</button>
      <button onClick={stopVoiceRecognition}>Stop Voice Recognition</button>
    </div>
  );
};

export default ChatBot;
